function DisplayBareRelations(WebPage $oPage, $bEditMode = false) {
                        parent::DisplayBareRelations($oPage, $bEditMode);
                        if (!$bEditMode)  {
                            if ($this instanceof ActionRule)  {
                                $oPage->SetCurrentTab(Dict::S('Class:ActionRule/Title:Tester'));
                                $sUrl =  utils::GetAbsoluteUrlModulePage('dlp-global-rules', 'DlpGlobalRulesTester.php') 
                                    . '&action=tester&oid=' . $this->GetKey();
                                $oPage->add("<div id='tester' class='resizable' style='height:1000px;'>
                                <iframe src='" . $sUrl. "' style='width:100%;height:100%;'></iframe>
                                </div>");
                            }
                        }
                    }

	static function parseValuesToApply($oObj)
                    {
                        $aDatas = [];
                        $sValuesToApply = $oObj->Get('values_to_apply');
                        if (trim($sValuesToApply) === '') {
                            return false;
                        }
                        $aValuesToApply = explode(PHP_EOL, $sValuesToApply);
                        foreach ($aValuesToApply as $sType) {
                            $aType = explode(DLP_RULES_TYPE_SEPARATOR, trim($sType));
                            if (isset($aType[0])) {
                                switch($aType[0]) {
                                case 'value':
                                    if (count($aType) === 2) {
                                        $aValue = explode(DLP_RULES_VALUE_SEPARATOR, $aType[1]);
                                        if (count($aValue) == 2) {
                                            $aDatas[] = [
                                                'type' => 'value',
                                                'col' => $aValue[0],
                                                'value' => $aValue[1],
                                            ];
                                        } else {
                                            return false;
                                        }
                                    } else {
                                        return false;
                                    }
                                    break;
                                case 'stimuli':
                                    if (count($aType) === 2) {
                                        $aValue = explode(DLP_RULES_VALUE_SEPARATOR, $aType[1]);
                                        if (count($aValue) == 2) {
                                            $aDatas[] = [
                                                'type' => 'stimuli',
                                                'value' => $aValue[1],
                                            ];
                                        } else {
                                            return false;
                                        }
                                    } else {
                                        return false;
                                    }
                                    break;
                                case 'link':
                                    if (count($aType) === 3) {
                                        $aTmp = [
                                            'type' => 'link',
                                            'col' => $aType[1],
                                            'value' => [],
                                        ];
                                        foreach (explode(DLP_RULES_LINK_VALUE_SEPARATOR, $aType[2]) as $sValue) {
                                            $aTmp['value'][] = $sValue;
                                        }
                                        $aDatas[] = $aTmp;
                                    } else {
                                        return false;
                                    }
                                    break;
                                default:
                                    break;
                                }
                            } else {
                                return false;
                            }
                        }
                        return $aDatas;
                    }
