function DisplayBareRelations(WebPage $oPage, $bEditMode = false) {
                        parent::DisplayBareRelations($oPage, $bEditMode);
                        if (!$bEditMode)  {
                            if ($this instanceof ActionRule)  {
                                $oPage->SetCurrentTab(Dict::S('Class:ActionRule/Title:Tester'));
                                $sUrl =  utils::GetAbsoluteUrlModulePage('dlp-global-rules', 'tester.dlp-global-rules.php') 
                                    . '&action=tester&oid=' . $this->GetKey();
                                $oPage->add("<div id='tester' class='resizable' style='height:1000px;'>
                                <iframe src='" . $sUrl. "' style='width:100%;height:100%;'></iframe>
                                </div>");
                            }
                        }
                    }

	static function parseValuesToApply($oObj)
                    {
                        $aDatas = [];
                        $sValuesToApply = $oObj->Get('values_to_apply');
                        if (trim($sValuesToApply) === '') {
                            return false;
                        }
                        $aValuesToApply = explode(PHP_EOL, $sValuesToApply);
                        foreach ($aValuesToApply as $sValue) {
                            $aValue = explode(':', trim($sValue));
                            if (isset($aValue[0])) {
                                switch($aValue[0]) {
                                case 'value':
                                    if (count($aValue) === 3) {
                                        $aDatas[] = [
                                            'type' => 'value',
                                            'col' => $aValue[1],
                                            'value' => $aValue[2],
                                        ];
                                    } else {
                                        return false;
                                    }
                                    break;
                                case 'stimuli':
                                    if (count($aValue) === 2) {
                                        $aDatas[] = [
                                            'type' => 'stimuli',
                                            'value' => $aValue[1],
                                        ];
                                    } else {
                                        return false;
                                    }
                                    break;
                                case 'link':
                                    if (count($aValue) >= 3) {
                                        $aTmp = [
                                            'type' => 'link',
                                            'col' => $aValue[1],
                                            'value' => [],
                                        ];
                                        // look for values and set them
                                        foreach ($aValue as $iK => $sVal) {
                                            if ($iK !== 0 && $iK !== 1)
                                            $aTmp['value'][] = $sVal;
                                        }
                                        $aDatas[] = $aTmp;
                                    } else {
                                        return false;
                                    }
                                    break;
                                default:
                                    break;
                                }
                            } else {
                                return false;
                            }
                        }
                        return $aDatas;
                    }
